from datetime import datetime

'''
strips iso date format into a more digestible date format
'''


def format_date(date):
    date = date[:-6]
    initial_format = "%Y-%m-%dT%H:%M:%S"
    date_format = "%d/%m/%Y - %H:%M:%S"
    iso_date = datetime.strptime(date, initial_format)
    return iso_date.strftime(date_format)
