"""
	A class used to represent a pog SCR match
"""


class Match:
    def __init__(self, map_name, description, players, match_type, match_date, match_winner):

        self.map_name = map_name
        self.map_description = description
        self.players = players
        self.match_type = match_type
        self.match_date = match_date
        self.match_winner = match_winner

    def show(self):
        return self.name

    def attributes(self):
        return [vars(self)[i] for i in vars(self)]

    @staticmethod
    def is_one_vs_one(match_as_json):
        return (len(match_as_json['Header']['Players']) == 2)  # if it's a 1v1 match
                # if the type is Top vs Bottom it represents a ladder match
                # and match_as_json['Header']['Type']['Name'] == "Top vs Bottom"
                #and match_as_json['Header']['Type']['ID'] == 15) #represents a ladder match 

    @staticmethod
    def get_player_index(match_as_json, YOUR_LADDER_NAMES):
        if match_as_json['Header']['Players'][0]['Name'] in YOUR_LADDER_NAMES:
            player_index = 0
            opponent_index = 1
        else:
            player_index = 1
            opponent_index = 0

        return (player_index, opponent_index)
