"""
	A class used to represent an SCR Player within a replay
"""


class Player:
    def __init__(self, username, race, apm, eapm, stats, player_id):
        self.username = username
        self.race = race
        self.apm = str(apm)
        self.eapm = str(eapm)
        self.stats = stats
        self.player_id = player_id

    def show(self):
        return(f"{self.username} - {self.race} - {self.apm} - {self.eapm} - {self.stats}")

    def attributes(self):
        return [vars(self)[i] for i in vars(self)]
