"""
	A class used to represent an SCR Player within a replay
"""


class Player:
    def __init__(self, username, race, apm, eapm, stats, player_team, match_outcome):
        self.username = username
        self.race = race
        self.apm = apm
        self.eapm = eapm
        self.stats = stats
        self.player_team = player_team
        self.match_outcome = match_outcome # if playerw on or lost
