"""
	A class used to represent an SCR Player within a replay
"""


class Player:
    def __init__(self, username, race, apm, eapm, stats, player_id):
        self.username = username
        self.race = race
        self.apm = apm
        self.eapm = eapm
        self.stats = stats
        self.player_id = player_id
