    def load_data(self):
        for count, game in enumerate(replay_data.match_history.match_history):
            number_of_rows = len(replay_data.match_history.match_history)
            self.tableWidget.setRowCount(number_of_rows - 1)
            self.tableWidget.setItem(
                count, 0, QtWidgets.QTableWidgetItem(game.players[0].username))
            self.tableWidget.setItem(
                count, 1, QtWidgets.QTableWidgetItem(f"{game.players[0].apm}/{game.players[0].eapm}"))
            self.tableWidget.setItem(
                count, 2, QtWidgets.QTableWidgetItem(game.players[1].username))
            self.tableWidget.setItem(
                count, 3, QtWidgets.QTableWidgetItem(f"{game.players[1].apm}/{game.players[1].eapm}"))
            self.tableWidget.setItem(
                count, 4, QtWidgets.QTableWidgetItem(game.players[1].race))
            self.tableWidget.setItem(
                count, 5, QtWidgets.QTableWidgetItem(game.map_name))
            self.tableWidget.setItem(
                count, 6, QtWidgets.QTableWidgetItem(game.match_winner))
            self.tableWidget.setItem(
                count, 7, QtWidgets.QTableWidgetItem(game.match_date))

